pop_ttl <- pop_tbl %>% 
  filter(시도 == "전국") %>% 
  mutate(나이_2027 = 나이 + 4) %>% 
  mutate(인구수_2027 = lag(인구수, n = 4)) %>% 
  drop_na() 

pop_now <- pop_ttl %>% 
  select(나이, 인구수) %>% 
  mutate(시점 = "현재")

pop_future <- pop_ttl %>% 
  select(나이 = 나이_2027, 인구수 = 인구수_2027)  %>% 
  mutate(시점 = "4년후")

proj_tbl <- bind_rows(pop_now, pop_future)

proj_tbl %>% 
  ggplot(aes(x = 나이, y = 인구수, fill = 시점)) +
    geom_col(position = "dodge") +
    scale_fill_manual(values = c("현재" = "gray20",
